package sec2_verify07;

public class PrinterExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Printer pt = new Printer();
		System.out.print("숫자값 출력 : ");
		pt.println(10); 
		System.out.print("논리값 출력 : ");
		pt.println(true); 
		System.out.print("더블형값 출력 : ");
		pt.println(5.7); 
		System.out.print("스트링  출력 : ");
		pt.println("홍길동"); 

	}

}
